mapper.select:
  	     select (select introduce from tb_user where user_id = #{userId}) as introduce,
  	            (select count(*) from tb_painting where artist_id = #{userId}) as uploadCount,
  	            (select count(*) 
  	               from tb_purchase 
  	              where user_id = #{userId}
  	                and purchase_status in 
		    								   #{status}
		                            	   ) as postCount
           from dual
